import { base64 } from "multiformats/bases/base64";
import SDK from "@hyperledger/identus-sdk";
// convert raw DIDComm message to domain, handles parsing idiosyncrasies
const convertDidcomm = (value: any) => new SDK.Domain.Message(
  value.body,
  value.id,
  value.type,
  typeof value.from === "string" ? SDK.Domain.DID.fromString(value.from) : undefined,
  typeof value.to?.at(0) === "string" ? SDK.Domain.DID.fromString(value.to?.at(0)) : undefined,
  // this.parseAttachmentsToDomain(value.attachments ?? []),
  (value.attachments ?? []).map(x => ({
    ...x,
    data: {
      base64: x.data.base64,
      data: x.data.json,
    }
  })),
  value.thid,
  value.extraHeaders,
  value.created_time,
  value.expires_time,
  [],
  undefined,
  value.from_prior,
  value.pthid
);

// create a mocked attachment from a Domain Message
const msgToAttachment = (value: SDK.Domain.Message) => {
  const obj = {
    ...value,
    to: value.to?.toString(),
    from: value.from?.toString()
  };
  const encoded = Buffer.from(JSON.stringify(obj)).toString("base64");
  const mocked = base64.baseEncode(Buffer.from(encoded)).toString();
  return mocked;
};

